import styled, { css } from 'styled-components';


export const StyledIdentification = styled.main`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-around;
  flex-direction: row;

`;




export const Form = styled.form`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  width: 100vw;
  height: 100%;
  padding: 2rem 0;

  span{
    margin: 2rem 0 0.5rem 0; 
    width: 30%;

    min-width: 30rem;



    input,select, option{
        font-weight: bold;
        width: 100%;
        height:  5rem;
        padding: 1rem;
        margin-top: 1rem ;
        background: #F8F8F2;
        border: 0;
        color: #44475A;


        ::placeholder{
        color: #44475A;

        }
    }

  }

`;


export const Text = styled.h1`

  ${props => props.label && css`
  font-size: 2rem;
  color: #F8F8F2;

  `};


  ${props => props.tecTitle && css`

  font-size: min(14vw, 6rem); 
  margin-bottom: 10rem;  
  color: #F8F8F2;
  text-align: center;

  `};

`;


export const ButtonSubmit = styled.button`
  width: 30%;
  min-width: 30rem;
  height:  5rem;
  padding: 0 2rem;
  margin-top: 2rem;
  background: #BD93F9;
  color: #44475A;
  border: 0;
  font-weight: bold;


  cursor: pointer;
  transition: all .4s ease;

  &:hover{

    background: #44475A;
    color: #BD93F9;

  }
`;

export const Footer = styled.footer`

  display:flex;
  align-items: center;
  justify-content: center;
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 1rem 0;
  p,a{
    font-size: 1.4rem;
  }

  a{
    text-decoration: none;
    color: #BD93F9;
    transition: opacity .2s ease;

    &:hover{
      opacity: 0.5;
    }

  }
`;
