import React from 'react';

import { QuestionWrapper, Text } from './styles';

const Question = ({ question, answers, nextQuestion, countQuestion }) => {

  const {
    answer_a,
    answer_b,
    answer_c,
    answer_d,
    answer_e,
    answer_f
  } = answers;
  return (


    <QuestionWrapper>
      <Text count>Question: {countQuestion + 1}</Text>
      <Text quest>{question}</Text>


      {answers.answer_a && <Text answer>{answers.answer_a}</Text >}
      {answers.answer_b && <Text answer>{answers.answer_b}</Text >}
      {answers.answer_c && <Text answer>{answers.answer_c}</Text >}
      {answers.answer_d && <Text answer>{answers.answer_d}</Text >}
      {answers.answer_e && <Text answer>{answers.answer_e}</Text >}
      {answers.answer_f && <Text answer>{answers.answer_f}</Text >}


      <button onClick={nextQuestion}> Next</button>


    </QuestionWrapper>


  );
};

export default Question;
