import { createGlobalStyle } from 'styled-components';

const GlobalStyled = createGlobalStyle`

  *{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body,
  html{
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    background: #282A36;
    color: #F8F8F2;
font-family: 'Rubik', sans-serif;
  }

`;

export default GlobalStyled;
