import axios from 'axios';

import useUSer from '../../utils/useUser';


const Quiz = ({ quiz }) => {

  const { user } = useUSer();
  return (
    <>
      <h1>Quiz</h1>
      {console.log(quiz)}



      <div >

        {/* <h1>{quiz[1].question}</h1>
        <p>{quiz[1].answers.answer_a}</p>
