import styled from 'styled-components';



export const Text = styled.h1``;
export const StyledA = styled.a`
  display: flex;
  align-items: center;
  justify-content: center;
  color: #BD93F9;
  margin-top: 2rem;
  cursor: pointer;
  transition: opacity 0.2s ease;
  &:hover{
    opacity: 0.5;
  }
`;
