import styled, { css } from 'styled-components';


export const StyledIdentification = styled.main`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-around;
  flex-direction: row;

`;


export const Description = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
    width: 50%;

`;


export const Form = styled.form`
  display: flex;
  align-items: flex-start;
  justify-content: center;
  flex-direction: column;

  width: 50%;

  span{
    margin: 2rem 0 0.5rem 0; 
    width: 60%;



    input,select, option{
        width: 100%;
        height:  5rem;
        padding: 1rem;
        margin-top: 1rem ;
        background: #F8F8F2;
        border: 0;
        color: #44475A;

        ::placeholder{
        color: #44475A;

        }
    }
  }

`;


export const Text = styled.h1`
  font-size: 2.4rem;

  ${props => props.label && css`
  font-size: 2rem;

  `};
`;


export const ButtonSubmit = styled.button`

  width: 60%;
  height:  5rem;
  padding: 0 2rem;
  margin-top: 2rem;
  background: #BD93F9;
  color: #F8F8F2;
  border: 0;
  font-weight: bold;
  cursor: pointer;
`;
